def load_trimmed_logo(path: Path):
    """Load logo, trim outer whitespace, return ImageReader for ReportLab."""
    try:
        im = Image.open(path).convert("RGBA")
        # Create bbox from non-transparent and non-white pixels
        bg = Image.new("RGBA", im.size, (255,255,255,0))
        diff = Image.alpha_composite(bg, im)
        # Use alpha channel + luminance to find content
        # Compute bbox of pixels that are not almost-white and not fully transparent
        px = diff.getdata()
        w, h = diff.size
        xs = []
        ys = []
        for i,(r,g,b,a) in enumerate(px):
            if a > 10 and (r < 250 or g < 250 or b < 250):
                x = i % w
                y = i // w
                xs.append(x); ys.append(y)
        if xs and ys:
            bbox = (min(xs), min(ys), max(xs)+1, max(ys)+1)
            im = im.crop(bbox)
        bio = BytesIO()
        im.save(bio, format="PNG")
        bio.seek(0)
        return ImageReader(bio)
    except Exception:
        return ImageReader(str(path))

# Hebrew display helper for PDF (bidi)
def he(s: str) -> str:
    try:
        return get_display(str(s))
    except Exception:
        return str(s)

def ensure_hebrew_font():
    try:
        pdfmetrics.getFont("DejaVuSans")
        return
    except Exception:
        pass
    for fp in [
        "/usr/share/fonts/truetype/dejavu/DejaVuSans.ttf",
        "/usr/share/fonts/truetype/dejavu/DejaVuSansCondensed.ttf",
    ]:
        if Path(fp).exists():
            pdfmetrics.registerFont(TTFont("DejaVuSans", fp))
            return

def aggrid_editable(df: pd.DataFrame, editable_cols: list, key: str, height: int = None):
    gb = GridOptionsBuilder.from_dataframe(df)
    gb.configure_default_column(
        editable=False, resizable=True, wrapText=True, autoHeight=True,
        cellStyle={"textAlign": "right"}
    )
    for c in editable_cols:
        gb.configure_column(c, editable=True)
    gb.configure_grid_options(enableRtl=True, domLayout="normal", rowHeight=40, headerHeight=40, suppressRowTransform=True)
    gb.configure_side_bar(False)
    gridOptions = gb.build()
    resp = AgGrid(
        df,
        gridOptions=gridOptions,
        data_return_mode=DataReturnMode.FILTERED_AND_SORTED,
        update_mode=GridUpdateMode.VALUE_CHANGED,
        fit_columns_on_grid_load=True,
        allow_unsafe_jscode=False,
        key=key,
        height=height,
    )
    return pd.DataFrame(resp["data"])

def aggrid_view(df: pd.DataFrame, key: str, height: int = None):
    gb = GridOptionsBuilder.from_dataframe(df)
    gb.configure_default_column(
        editable=False, resizable=True, wrapText=True, autoHeight=True,
        cellStyle={"textAlign": "right"}
    )
    gb.configure_grid_options(enableRtl=True, domLayout="normal", rowHeight=40, headerHeight=40, suppressRowTransform=True)
    gb.configure_side_bar(False)
    gridOptions = gb.build()
    AgGrid(
        df,
        gridOptions=gridOptions,
        data_return_mode=DataReturnMode.FILTERED_AND_SORTED,
        update_mode=GridUpdateMode.NO_UPDATE,
        fit_columns_on_grid_load=True,
        allow_unsafe_jscode=False,
        key=key,
        height=height,
    )

