def set_value_safe(ws, addr: str, value):
    """Writes safely to merged cells by writing to the top-left cell of the merged range."""
    cell = ws[addr]
    if isinstance(cell, MergedCell):
        for merged in ws.merged_cells.ranges:
            if addr in merged:
                ws[merged.start_cell.coordinate].value = value
                return
        ws.cell(row=cell.row, column=cell.column).value = value
    else:
        cell.value = value

def load_template_xlsx(uploaded_file) -> openpyxl.Workbook:
    return openpyxl.load_workbook(uploaded_file, data_only=False)

def time_to_hours(t):
    if t is None:
        return 0.0
    if isinstance(t, time):
        return t.hour + t.minute/60 + t.second/3600
    return float(getattr(t, "hour", 0)) + float(getattr(t, "minute", 0))/60 + float(getattr(t, "second", 0))/3600

def mround(value: float, multiple: float) -> float:
    if multiple == 0:
        return value
    return round(value / multiple) * multiple

def currency(n: float) -> str:
    return f"{n:,.0f} ₪".replace(",", ",")

def currency2(n: float) -> str:
    return f"{n:,.2f} ₪".replace(",", ",")

def discount_factor(qty: int):
    """Returns (factor, percent) with exact steps: 10%, 20%, 25%."""
    if 1 < qty <= 30:
        return 0.90, 10
    if 30 < qty <= 100:
        return 0.80, 20
    if qty > 100:
        return 0.75, 25
    return 1.00, 0

def read_rates_from_sheet(wb: openpyxl.Workbook):
    ws = wb.active

    # Materials table in rows 6-9: C name, D price per kg
    materials = {}
    for r in range(6, 10):
        name = ws.cell(r, 3).value
        per_kg = ws.cell(r, 4).value
        if name:
            materials[str(name)] = float(per_kg or 0.0)

    # Work rates in rows 13-15: C name, D price per hour
    work = {}
    for r in range(13, 16):
        name = ws.cell(r, 3).value
        per_h = ws.cell(r, 4).value
        if name:
            work[str(name)] = float(per_h or 0.0)

    # Add-ons prices in rows 18-20: C name, D price
    addons = {}
    for r in range(18, 21):
        name = ws.cell(r, 3).value
        price = ws.cell(r, 4).value
        if name:
            addons[str(name)] = float(price or 0.0)

    return materials, work, addons

@dataclass
class Inputs:
    project_name: str
    material_lines: list  # list of dicts: {"חומר": str, "גרמים": float}
    modeling_time: time
    printing_time: time
    assembly_time: time
    magnets_qty: int
    led_single_qty: int
    led_desk_qty: int
    units_qty: int

def compute(inputs: Inputs, materials_per_kg: dict, work_per_h: dict, addons_price: dict):
    per_g = {k: float(v)/1000 for k, v in materials_per_kg.items()}

    rows = []
    mat_total = 0.0

    # Materials (filaments)
    for i, line in enumerate(inputs.material_lines, start=1):
        mat = line["חומר"]
        grams = float(line.get("גרמים", 0) or 0)
        unit = float(per_g.get(mat, 0.0))  # ₪ per gram
        cost = unit * grams
        rows.append({
            "קטגוריה": "פילמנטים",
            "פריט": mat,            "כמות": grams,
            "יחידה": "גרם",
            "מחיר ליחידה": unit,
            "עלות": cost
        })
        mat_total += cost

    # Labor (work)
    modeling_h = time_to_hours(inputs.modeling_time)
    printing_h = time_to_hours(inputs.printing_time)
    assembly_h = time_to_hours(inputs.assembly_time)

    def labor_row(name_he: str, hours: float):
        unit = float(work_per_h.get(name_he, 0.0))  # ₪ per hour
        return {
            "קטגוריה": "עבודה",
            "פריט": name_he,            "כמות": hours,
            "יחידה": "שעה",
            "מחיר ליחידה": unit,
            "עלות": unit * hours
        }

    labor_rows = [
        labor_row("מידול", modeling_h),
        labor_row("הדפסה", printing_h),
        labor_row("הרכבה", assembly_h),
    ]
    rows.extend(labor_rows)

    labor_total = sum(r["עלות"] for r in labor_rows)
    modeling_cost = labor_rows[0]["עלות"]

    # Add-ons
    magnets_q = int(inputs.magnets_qty or 0)
    led_single_q = int(inputs.led_single_qty or 0)
    led_desk_q = int(inputs.led_desk_qty or 0)

    def addon_row(name: str, qty: int):
        unit = float(addons_price.get(name, 0.0))
        return {
            "קטגוריה": "תוספות",
            "פריט": name,            "כמות": qty,
            "יחידה": "יח׳",
            "מחיר ליחידה": unit,
            "עלות": unit * qty
        }

    addon_rows = [
        addon_row("מגנטים (שקל/מגנט)", magnets_q),
        addon_row("לד בודד", led_single_q),
        addon_row("לד שולחני", led_desk_q),
    ]
    rows.extend(addon_rows)
    addons_total = sum(r["עלות"] for r in addon_rows)

    cost_sum_all = mat_total + labor_total + addons_total
    unit_price_excl_modeling = (cost_sum_all - modeling_cost)

    qty = int(inputs.units_qty or 0)
    disc, disc_pct = discount_factor(qty)
    total_before_discount = modeling_cost + unit_price_excl_modeling * qty
    total = mround(total_before_discount * disc, 5)

    df = pd.DataFrame(rows)
    return {
        "breakdown_df": df,
        "materials_total": mat_total,
        "labor_total": labor_total,
        "addons_total": addons_total,
        "modeling_cost": modeling_cost,
        "unit_price": unit_price_excl_modeling,
        "qty": qty,
        "discount": disc,
        "discount_pct": disc_pct,
        "total_before_discount": total_before_discount,
        "total": total,
        "project": inputs.project_name,
    }

def _register_hebrew_font():
    # Prefer DejaVuSans (usually available on Streamlit Cloud)
    candidates = [
        "/usr/share/fonts/truetype/dejavu/DejaVuSans.ttf",
        "/usr/share/fonts/truetype/dejavu/DejaVuSansCondensed.ttf",
    ]
    for fp in candidates:
        if Path(fp).exists():
            try:
                pdfmetrics.registerFont(TTFont("DejaVu", fp))
                return "DejaVu"
            except Exception:
                pass
    return "Helvetica"

def render_pdf(result: dict) -> bytes:
    buf = BytesIO()
    ensure_hebrew_font()
    c = canvas.Canvas(buf, pagesize=A4)
    width, height = A4

    x = 18*mm
    y = height - 18*mm

    # Logo (top-left, huge - trimmed)
    logo_path = Path(__file__).parent / "logo.jpeg"
    if logo_path.exists():
        logo_img = load_trimmed_logo(logo_path)
        logo_w = 150*mm
        logo_h = 60*mm
        c.drawImage(logo_img, x, y - logo_h, width=logo_w, height=logo_h, preserveAspectRatio=True, mask='auto')
    y -= 65*mm

    c.setFont("DejaVuSans", 18)
    c.drawRightString(width - x, y, he("הצעת מחיר - הדפסת תלת מימד"))
    y -= 10*mm
    c.setFont("DejaVuSans", 12)
    c.drawRightString(width - x, y, he(f"פרויקט: {result['project']}"))
    y -= 8*mm
    c.drawRightString(width - x, y, he(f"תאריך: {pd.Timestamp.now().strftime('%d/%m/%Y')}"))
    y -= 14*mm

    c.setFont("DejaVuSans", 12)
    c.drawRightString(width - x, y, he(f"סה\"כ מידול: {currency(result['modeling_cost'])}"))
    y -= 7*mm

    c.drawRightString(width - x, y, he(f"מחיר יחידה (ללא מידול): {currency(result['unit_price'])}"))
    y -= 7*mm
    c.drawRightString(width - x, y, he(f"כמות: {result['qty']}"))
    y -= 7*mm
    c.drawRightString(width - x, y, he(f"הנחת כמות: {int(result.get('discount_pct', round((1-result['discount'])*100)))}%"))
    y -= 7*mm
    c.setFont("DejaVuSans", 14)
    c.drawRightString(width - x, y, he(f"סה\"כ: {currency(result['total'])}"))
    y -= 12*mm

    df = result["breakdown_df"].copy()
    df = df[["קטגוריה","פריט","כמות","יחידה","מחיר ליחידה","עלות"]]
    df = df[df["כמות"].astype(float) != 0].reset_index(drop=True)

    c.setFont("DejaVuSans", 11)
    c.drawRightString(width - x, y, he("פירוט עלויות"))
    y -= 8*mm

    c.setFont("DejaVuSans", 9)
    col_right = {
        "קטגוריה": width - x,
        "פריט": width - x - 32*mm,
        "כמות": width - x - 86*mm,
        "מחיר ליחידה": width - x - 112*mm,
        "עלות": width - x - 140*mm,
    }

    # Header
    c.drawRightString(col_right["קטגוריה"], y, he("קטגוריה"))
    c.drawRightString(col_right["פריט"], y, he("פריט"))
    c.drawRightString(col_right["כמות"], y, he("כמות"))
    c.drawRightString(col_right["מחיר ליחידה"], y, he("מחיר ליחידה"))
    c.drawRightString(col_right["עלות"], y, he("עלות"))
    y -= 6*mm

    for _, r in df.iterrows():
        if y < 18*mm:
            c.showPage()
            ensure_hebrew_font()
            c.setFont("DejaVuSans", 9)
            y = height - 20*mm

        c.drawRightString(col_right["קטגוריה"], y, he(str(r["קטגוריה"])))
        c.drawRightString(col_right["פריט"], y, he(str(r["פריט"])))
        qty_txt = f'{float(r["כמות"]):.2f}'.rstrip("0").rstrip(".")
        c.drawRightString(col_right["כמות"], y, he(f'{qty_txt} {r["יחידה"]}'))
        c.drawRightString(col_right["מחיר ליחידה"], y, he(currency2(float(r["מחיר ליחידה"]))))
        c.drawRightString(col_right["עלות"], y, he(currency2(float(r["עלות"]))))
        y -= 5.2*mm

    c.showPage()
    c.save()
    return buf.getvalue()

def write_back_to_xlsx(template_wb: openpyxl.Workbook, inputs: Inputs, result: dict,
                       materials_per_kg: dict, work_per_h: dict, addons_price: dict) -> bytes:
    ws = template_wb.active

    # Project
    set_value_safe(ws, "H5", inputs.project_name)

    # Prices back into the template tables (by row order 6-9 / 13-15 / 18-20)
    for r in range(6, 10):
        name = ws.cell(r, 3).value
        if name and str(name) in materials_per_kg:
            ws.cell(r, 4).value = float(materials_per_kg[str(name)])

    for r in range(13, 16):
        name = ws.cell(r, 3).value
        if name and str(name) in work_per_h:
            ws.cell(r, 4).value = float(work_per_h[str(name)])

    for r in range(18, 21):
        name = ws.cell(r, 3).value
        if name and str(name) in addons_price:
            ws.cell(r, 4).value = float(addons_price[str(name)])

    # Material selections and grams (H7..H10, I7..I10)
    base_row = 7
    for i, line in enumerate(inputs.material_lines):
        ws.cell(base_row + i, 8).value = line["חומר"]  # H
        ws.cell(base_row + i, 9).value = float(line.get("גרמים", 0) or 0)  # I

    # Times (I11..I13)
    set_value_safe(ws, "I11", inputs.modeling_time)
    set_value_safe(ws, "I12", inputs.printing_time)
    set_value_safe(ws, "I13", inputs.assembly_time)

    # Add-ons qty (I14..I16)
    set_value_safe(ws, "I14", int(inputs.magnets_qty or 0))
    set_value_safe(ws, "I15", int(inputs.led_single_qty or 0))
    set_value_safe(ws, "I16", int(inputs.led_desk_qty or 0))

    # Units qty (J21)
    set_value_safe(ws, "J21", int(inputs.units_qty or 0))

    # Also write computed outputs for convenience
    set_value_safe(ws, "J18", float(result["modeling_cost"]))
    set_value_safe(ws, "J20", float(result["unit_price"]))
    set_value_safe(ws, "J22", float(result["discount"]))
    set_value_safe(ws, "J24", float(result["total"]))

    out = BytesIO()
    template_wb.save(out)
    return out.getvalue()


def num_stepper(label: str, key: str, value, step, min_value, is_int: bool = False):
    """
    Mobile-friendly +/- controls (work even when native steppers don't).
    Stores canonical value in st.session_state[key].
    """
    if key not in st.session_state:
        st.session_state[key] = int(value) if is_int else float(value)
    if f"{key}_input" not in st.session_state:
        st.session_state[f"{key}_input"] = st.session_state[key]

    c1, c2, c3 = st.columns([1, 5, 1], vertical_alignment="center")
    with c1:
        if st.button("−", key=f"{key}_minus"):
            v = st.session_state[key] - step
            if v < min_value:
                v = min_value
            v = int(v) if is_int else float(v)
            st.session_state[key] = v
            st.session_state[f"{key}_input"] = v
    with c3:
        if st.button("+", key=f"{key}_plus"):
            v = st.session_state[key] + step
            v = int(v) if is_int else float(v)
            st.session_state[key] = v
            st.session_state[f"{key}_input"] = v
    with c2:
        v = st.number_input(
            label,
            min_value=min_value,
            step=step,
            value=st.session_state[f"{key}_input"],
            key=f"{key}_input",
        )
        v = int(v) if is_int else float(v)
        st.session_state[key] = v
    return st.session_state[key]

